con:configuration.scriptText:
////Initialise utils
import com.eviware.soapui.support.XmlHolder;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)
def expectedUSD_GBPRate = "0.5967" //// As per technical test
////Make sure header response is 200
def headers = messageExchange.getResponseHeaders()
assert['HTTP/1.1 200 OK'] == headers["#status#"]

////Get response in xml
holder.namespaces["soap"] = "http://schemas.xmlsoap.org/soap/envelope/"
def conversionXml = holder.getDomNode("//soap:Envelope")

def holder_ = new XmlHolder(conversionXml)

holder_.declareNamespace('xn',"http://tempuri.org/")
def conversionRate = holder_.getNodeValue("//xn:GetConversionRateResult")
///check conversion rate contains a value
if(conversionRate!= null){ 
	log.info "Conversion USD - GBP is " + conversionRate
	}
else{ 
	log.error "Conversion rate result not found"
	}

if (conversionRate != expectedUSD_GBPRate){
	log.info "Expected " + expectedUSD_GBPRate +" as Conversion Rate for USD - GBP but instead returned " + conversionRate
	log.error "Conversion rate returned doesnt match expected"
	assert expectedUSD_GBPRate == conversionRate
}
else
{
	assert expectedUSD_GBPRate == holder_.getNodeValue("//xn:GetConversionRateResult") 
}
